import { useState, useEffect } from "react";
import { AuthProvider, useAuth } from "./contexts/AuthContext";
import Login from "./pages/Login";
import Home from "./pages/Home";
import AdminPanel from "./pages/AdminPanel";
import Register from "./pages/Register";

// Componente que usa o contexto de autenticação
function AppContent() {
  const { currentUser, logout } = useAuth();
  const [error, setError] = useState("");

  const handleLogout = async () => {
    try {
      setError("");
      await logout();
    } catch (error) {
      setError("Falha ao fazer logout: " + error.message);
    }
  };

  if (!currentUser) {
    return <Login />;
  }

  if (currentUser.role === "admin") {
    return <AdminPanel user={currentUser} onLogout={handleLogout} />;
  }

  return <Home user={currentUser} onLogout={handleLogout} />;
}

// Componente principal que fornece o contexto de autenticação
function App() {
  return (
    <AuthProvider>
      <AppContent />
    </AuthProvider>
  );
}

export default App;
