import { books } from "../data/books";
import { useState } from "react";

export default function Home({ user, onLogout }) {
  const [favorites, setFavorites] = useState([]);

  const toggleFavorite = (id) => {
    setFavorites((prev) =>
      prev.includes(id) ? prev.filter((fav) => fav !== id) : [...prev, id]
    );
  };

  return (
    <div className="p-6">
      <div className="flex justify-between mb-4">
        <h1 className="text-2xl font-bold">Bem-vindo, {user.username}</h1>
        <button onClick={onLogout} className="bg-red-500 text-white px-4 py-1 rounded">
          Sair
        </button>
      </div>
      <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
        {books.map((book) => (
          <div key={book.id} className="border p-4 rounded shadow">
            <h2 className="text-xl font-semibold">{book.title}</h2>
            <p className="text-sm text-gray-600">por {book.author}</p>
            <p className="mt-2">{book.description}</p>
            <button
              onClick={() => toggleFavorite(book.id)}
              className={`mt-3 px-3 py-1 rounded ${
                favorites.includes(book.id)
                  ? "bg-yellow-400"
                  : "bg-gray-300"
              }`}
            >
              {favorites.includes(book.id) ? "Favoritado" : "Favoritar"}
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}
