import React, { createContext, useState, useContext, useEffect } from 'react';
import { 
  createUserWithEmailAndPassword, 
  signInWithEmailAndPassword, 
  signOut, 
  onAuthStateChanged 
} from 'firebase/auth';
import { doc, getDoc, setDoc } from 'firebase/firestore';
import { auth, db } from '../firebase/config';

const AuthContext = createContext();

export function useAuth() {
  return useContext(AuthContext);
}

export function AuthProvider({ children }) {
  const [currentUser, setCurrentUser] = useState(null);
  const [loading, setLoading] = useState(true);

  async function signup(email, password, username, role = 'user') {
    if (!auth || !db) {
      throw new Error('Firebase não está inicializado corretamente');
    }

    try {
      // Criar usuário no Authentication
      const userCredential = await createUserWithEmailAndPassword(auth, email, password);
      const user = userCredential.user;

      // Criar documento do usuário no Firestore
      const userRef = doc(db, 'users', user.uid);
      await setDoc(userRef, {
        username,
        role,
        email,
        createdAt: new Date().toISOString()
      });

      // Atualizar o estado do usuário atual
      setCurrentUser({
        ...user,
        username,
        role
      });

      return userCredential;
    } catch (error) {
      console.error('Erro no signup:', error);
      throw error;
    }
  }

  async function login(email, password) {
    if (!auth || !db) {
      throw new Error('Firebase não está inicializado corretamente');
    }

    try {
      const userCredential = await signInWithEmailAndPassword(auth, email, password);
      const user = userCredential.user;

      // Buscar dados adicionais do usuário no Firestore
      const userDoc = await getDoc(doc(db, 'users', user.uid));

      if (userDoc.exists()) {
        const userData = userDoc.data();
        setCurrentUser({
          ...user,
          ...userData
        });
      } else {
        setCurrentUser(user);
      }

      return userCredential;
    } catch (error) {
      console.error('Erro no login:', error);
      throw error;
    }
  }

  function logout() {
    if (!auth) {
      throw new Error('Firebase não está inicializado corretamente');
    }
    return signOut(auth);
  }

  useEffect(() => {
    if (!auth || !db) {
      console.error('Firebase não está inicializado corretamente');
      setLoading(false);
      return;
    }

    const unsubscribe = onAuthStateChanged(auth, async (user) => {
      if (user) {
        try {
          const userDoc = await getDoc(doc(db, 'users', user.uid));

          if (userDoc.exists()) {
            setCurrentUser({
              ...user,
              ...userDoc.data()
            });
          } else {
            setCurrentUser(user);
          }
        } catch (error) {
          console.error('Erro ao buscar dados do usuário:', error);
          setCurrentUser(user);
        }
      } else {
        setCurrentUser(null);
      }

      setLoading(false);
    });

    return () => unsubscribe();
  }, []);

  const value = {
    currentUser,
    signup,
    login,
    logout
  };

  return (
    <AuthContext.Provider value={value}>
      {!loading && children}
    </AuthContext.Provider>
  );
}
