import { useState, useEffect } from "react";
import { useAuth } from "../contexts/AuthContext";
import { getBooks, addBook, deleteBook } from "../services/bookService";
import { searchBooks } from "../services/bookService";
import Navbar from "../components/Navbar";

export default function AdminPanel() {
  const { currentUser, logout } = useAuth();
  const [books, setBooks] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [newBook, setNewBook] = useState({
    title: "",
    author: "",
    description: "",
    imageUrl: "",
  });

  useEffect(() => {
    fetchBooks();
  }, []);

  useEffect(() => {
    console.log("Estado searchResults atualizado:", searchResults);
  }, [searchResults]);

  const handleLogout = async () => {
    try {
      await logout();
    } catch (error) {
      console.error("Erro ao fazer logout:", error);
    }
  };

  const fetchBooks = async () => {
    try {
      setLoading(true);
      console.log("Buscando livros da biblioteca");
      const fetchedBooks = await getBooks();
      console.log("Livros encontrados:", fetchedBooks.length);
      setBooks(fetchedBooks);
    } catch (error) {
      console.error("Erro ao buscar livros:", error);
      setError("Não foi possível carregar os livros. Tente novamente mais tarde.");
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = async () => {
    if (!searchTerm.trim()) return;

    try {
      setLoading(true);
      console.log("Pesquisando por:", searchTerm);
      const results = await searchBooks(searchTerm);
      console.log("Resultados da pesquisa:", results);
      setSearchResults(results);
    } catch (error) {
      console.error("Erro na pesquisa:", error);
      setError("Erro ao pesquisar livros. Tente novamente.");
    } finally {
      setLoading(false);
    }
  };

  const handleAddBook = async (book) => {
    try {
      setLoading(true);
      console.log("Adicionando livro à biblioteca:", book);

      // Garantir que todos os campos necessários estejam presentes
      const bookToAdd = {
        title: book.title || "Título desconhecido",
        author: book.author || "Autor desconhecido",
        description: book.description || "Sem descrição disponível",
        imageUrl: book.imageUrl || "https://via.placeholder.com/150",
        publisher: book.publisher || "Editora desconhecida",
        publishedDate: book.publishedDate || "Data desconhecida",
        pageCount: book.pageCount || 0,
        categories: book.categories || [],
        language: book.language || "pt",
        googleBooksId: book.id // Salvar o ID do Google Books para referência
      };

      await addBook(bookToAdd);
      console.log("Livro adicionado com sucesso");

      setSearchResults([]);
      setSearchTerm("");
      fetchBooks();
    } catch (error) {
      console.error("Erro ao adicionar livro:", error);
      setError("Erro ao adicionar livro. Tente novamente.");
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteBook = async (bookId) => {
    if (!window.confirm("Tem certeza que deseja excluir este livro?")) return;

    try {
      setLoading(true);
      await deleteBook(bookId);
      fetchBooks();
    } catch (error) {
      console.error("Erro ao excluir livro:", error);
      setError("Erro ao excluir livro. Tente novamente.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <Navbar user={currentUser} onLogout={handleLogout} />
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-4">Painel Administrativo</h1>

          {/* Barra de pesquisa da API */}
          <div className="mb-8">
            <h2 className="text-xl font-semibold text-gray-800 mb-4">Pesquisar e Adicionar Livros</h2>
            <div className="flex gap-4">
              <input
                type="text"
                placeholder="Pesquisar livros na API..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                onKeyDown={(e) => {
                  if (e.key === 'Enter') {
                    handleSearch();
                  }
                }}
                className="flex-1 px-4 py-2 rounded-lg border border-gray-300 focus:ring-2 focus:ring-blue-500 focus:border-transparent outline-none"
              />
              <button
                onClick={handleSearch}
                disabled={loading}
                className="px-6 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors disabled:opacity-50"
              >
                {loading ? "Pesquisando..." : "Pesquisar"}
              </button>
            </div>
            {error && (
              <div className="mt-2 text-red-600">
                {error}
              </div>
            )}
          </div>

          {/* Resultados da pesquisa */}
          {searchTerm && !loading && (
            <div className="mb-8">
              <h3 className="text-lg font-medium text-gray-800 mb-4">
                Resultados da Pesquisa para "{searchTerm}"
              </h3>
              {searchResults.length > 0 ? (
                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
                  {searchResults.map((book) => (
                    <div key={book.id} className="bg-white rounded-xl shadow-lg p-6">
                      <div className="flex items-start gap-4">
                        <img
                          src={book.imageUrl}
                          alt={book.title}
                          className="w-24 h-32 object-cover rounded-lg"
                        />
                        <div className="flex-1">
                          <h3 className="font-semibold text-gray-900">{book.title}</h3>
                          <p className="text-sm text-gray-600">{book.author}</p>
                          <button
                            onClick={() => handleAddBook(book)}
                            className="mt-2 px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 transition-colors"
                          >
                            Adicionar à Biblioteca
                          </button>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="text-center py-8 bg-white rounded-lg shadow">
                  <p className="text-gray-600">Nenhum resultado encontrado para "{searchTerm}"</p>
                </div>
              )}
            </div>
          )}

          {/* Lista de livros existentes */}
          <div>
            <h2 className="text-xl font-semibold text-gray-800 mb-4">Livros na Biblioteca</h2>
            {loading ? (
              <div className="flex justify-center items-center py-12">
                <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500"></div>
              </div>
            ) : error ? (
              <div className="text-center py-12">
                <p className="text-red-600">{error}</p>
              </div>
            ) : books.length === 0 ? (
              <div className="text-center py-12 bg-white rounded-lg shadow">
                <p className="text-gray-600">Nenhum livro na biblioteca. Use a pesquisa acima para adicionar livros.</p>
              </div>
            ) : (
              <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
                {books.map((book) => (
                  <div key={book.id} className="bg-white rounded-xl shadow-lg p-6">
                    <div className="flex items-start gap-4">
                      <img
                        src={book.imageUrl}
                        alt={book.title}
                        className="w-24 h-32 object-cover rounded-lg"
                      />
                      <div className="flex-1">
                        <h3 className="font-semibold text-gray-900">{book.title}</h3>
                        <p className="text-sm text-gray-600">{book.author}</p>
                        <button
                          onClick={() => handleDeleteBook(book.id)}
                          className="mt-2 px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 transition-colors"
                        >
                          Excluir
                        </button>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
