import { useState, useEffect } from "react";
import { useAuth } from "../contexts/AuthContext";
import { searchBooks } from "../services/bookService";
import BookCard from "../components/BookCard";
import Navbar from "../components/Navbar";

const Home = ({ onLogout }) => {
  const [books, setBooks] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const { user } = useAuth();

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!searchTerm.trim()) return;

    setLoading(true);
    setError(null);

    try {
      const results = await searchBooks(searchTerm);
      setBooks(results);
    } catch (err) {
      setError("Erro ao buscar livros. Por favor, tente novamente.");
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <Navbar user={user} onLogout={onLogout} />
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-4">Biblioteca Digital</h1>
          <div className="relative">
            <form onSubmit={handleSearch} className="flex gap-4">
              <input
                type="text"
                placeholder="Busque por título, autor ou assunto..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="flex-1 p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
              <button
                type="submit"
                disabled={loading}
                className="bg-blue-500 text-white px-6 py-2 rounded-lg hover:bg-blue-600 transition-colors disabled:opacity-50"
              >
                {loading ? "Buscando..." : "Buscar"}
              </button>
            </form>
          </div>
        </div>

        {error && (
          <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative mb-4">
            {error}
          </div>
        )}

        {loading ? (
          <div className="flex justify-center items-center h-64">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500"></div>
          </div>
        ) : books.length > 0 ? (
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
            {books.map((book) => (
              <BookCard
                key={book.id}
                book={{
                  id: book.id,
                  title: book.volumeInfo.title,
                  author: book.volumeInfo.authors?.[0] || "Autor desconhecido",
                  description: book.volumeInfo.description || "Sem descrição disponível",
                  imageUrl: book.volumeInfo.imageLinks?.thumbnail || "https://via.placeholder.com/150x200",
                  favoritesCount: 0
                }}
              />
            ))}
          </div>
        ) : (
          <div className="text-center text-gray-500 mt-8">
            {searchTerm
              ? "Nenhum livro encontrado. Tente outra busca."
              : "Use a barra de busca acima para encontrar livros."}
          </div>
        )}
      </div>
    </div>
  );
};

export default Home;
