import { 
  collection, 
  doc, 
  getDocs, 
  getDoc, 
  addDoc, 
  updateDoc, 
  deleteDoc, 
  query, 
  where, 
  arrayUnion, 
  arrayRemove,
  increment
} from 'firebase/firestore';
import { db } from '../firebase/config';

// Referência para a coleção de livros
const booksCollection = collection(db, 'books');

const GOOGLE_BOOKS_API_KEY = process.env.REACT_APP_GOOGLE_BOOKS_API_KEY;
const GOOGLE_BOOKS_API_URL = 'https://www.googleapis.com/books/v1/volumes';

console.log("Chave da API do Google Books:", GOOGLE_BOOKS_API_KEY ? "Definida" : "Não definida");

export const searchBooks = async (searchTerm) => {
  try {
    console.log("Iniciando pesquisa na API do Google Books:", searchTerm);
    const url = `${GOOGLE_BOOKS_API_URL}?q=${searchTerm}&key=${GOOGLE_BOOKS_API_KEY}`;
    console.log("URL da requisição:", url);

    const response = await fetch(url);
    console.log("Resposta recebida:", response.status);

    const data = await response.json();
    console.log("Dados recebidos:", data);

    if (!data.items) {
      console.log("Nenhum resultado encontrado");
      return [];
    }

    const results = data.items.map(item => ({
      id: item.id,
      title: item.volumeInfo.title,
      author: item.volumeInfo.authors ? item.volumeInfo.authors[0] : 'Autor desconhecido',
      description: item.volumeInfo.description || 'Sem descrição disponível',
